import React, { useState } from 'react';
import { connect } from 'react-redux';

import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogTitle from '@material-ui/core/DialogTitle';
import Button from '@material-ui/core/Button';

import Fab from '@material-ui/core/Fab';
import ArrowRightIcon from '@material-ui/icons/ArrowRightAlt';

import FeedWidget from './components/feedWidget';

import './App.css';

const FeedConfigList = [
  {
    'version': 'v1',
    'route': 'merryjane',
    'url': 'https://merryjane.com/feed/all.rss',
    'image': 'logos/merry-jane-logo.png'
  },
  {
    'version': 'v1',
    'route': 'hightimes',
    'url': 'https://hightimes.com/news/feed/',
    'image': 'logos/high-times-logo.png'
  },
  {
    'version': 'v1',
    'route': 'cannalaw',
    'url': 'https://www.cannalawblog.com/feed/',
    'image': 'logos/canna-law-blog-log.png'
  },
  {
    'version': 'v2',
    'route': 'leafly',
    'url': 'https://www.leafly.com/feed',
    'image': 'logos/leafly-logo.png'
  },
  {
    'version': 'v1',
    'route': 'cannabist2',
    'url': 'https://www.thecannabist.co/feed/',
    'image': 'logos/cannabis-culture-logo.png'
  },
  {
    'version': 'v1',
    'route': 'marijuanadaily',
    'url': 'https://mjbizdaily.com/feed/',
    'image': 'logos/marijuana-daily-logo.png'
  },
  {
    'version': 'v2',
    'route': 'freshtoast',
    'url': 'https://thefreshtoast.com/feed/',
    'image': 'logos/fresh-toast-logo.png'
  },
  {
    'version': 'v1',
    'route': 'medicalmarijuana',
    'url': 'https://www.medicalmarijuanainc.com/feed/',
    'image': 'logos/medical-marijuana-logo.png'
  },
  {
    'version': 'v1',
    'route': 'grizzle',
    'url': 'https://grizzle.com/marijuana/feed/',
    'image': 'logos/grizzle-logo.png'
  },
  {
    'version': 'v1',
    'route': 'cannabisnet',
    'url': 'https://cannabis.net/rss/blog',
    'image': 'logos/cannabis-net-log.png'
  },
  {
    'version': 'v1',
    'route': 'marijuana',
    'url': 'https://www.marijuana.com/feed/',
    'image': 'logos/marijuana-com-logo.png'
  },
];

function App(props) {

  const [open, setOpen] = useState(false);
  const [linkActive, setLinkActive] = useState('');
  const [main, setMain] = useState(true);
  const [feedUrl, setFeedUrl] = useState(FeedConfigList);
  const [version, setVersion] = useState('v1');
  const [active, setActive] = useState('');
  const [rightBarOpen, setRightBar] = useState('');
  const [contentBarOpen, setContenttBar] = useState('');

  const menuClick = () => {
    setRightBar('rightBarOpen');
    setContenttBar('contentBarOpen');
  }

  const setStateChange = (state) => {
    setLinkActive(state);
  }

  const handleClose = () => {
    setOpen(false);
  }

  const handleClickOpen = () => {
    setOpen(true);
  }

  return (
    <div className="App">
      <div className="topbar desktop">
        <span><img src="logos/top-logo.png" alt="" /></span>
        <span><img src="logos/top-text-logo.png" alt="" /></span>
        <div className="Top-Right-Menu">
          <Fab variant="extended" aria-label="delete" className="mr-4" onClick={handleClickOpen}>
            Get The App
          </Fab>
          <span className="Login-Text" onClick={handleClickOpen}>Login</span>
        </div>
      </div>
      <div className="topbar mobile">
        <span><img src="logos/top-logo.png" alt="" /></span>
        <span><img src="logos/top-text-logo.png" alt="" /></span>
        {rightBarOpen !== 'rightBarOpen' ? (
          <span><img src="logos/top-menu-icon.png" onClick={menuClick} alt="" /></span>
        ) : (
            <span onClick={() => { console.log("fdfsd"); setRightBar(''); setContenttBar(''); }}><img src="logos/close-icon.png" alt="" /></span>
          )}
      </div>
      <div className="content">
        <div className={`sidebar ${rightBarOpen}`}>
          <ul className="navbar-nav mr-auto">
            {
              FeedConfigList.map((feed, index) => {
                return (
                  <li className={`nav-link ${linkActive}`} key={index} onClick={() => {
                    setRightBar('');
                    setContenttBar('');
                    setActive(feed.url);
                    setVersion(feed.version);
                    setFeedUrl(feed.url);
                    setMain(false);
                  }}>
                    {active === feed.url ? (
                      <ArrowRightIcon />
                    ) : (null)}
                    <img src={feed.image} alt="" />
                  </li>
                )
              })
            }
            {rightBarOpen ? (
              <li className="nav-link" onClick={handleClickOpen}>
                <Fab variant="extended" aria-label="delete">
                  Get The App
                </Fab>
              </li>
            ) : (
                null
              )}
            {rightBarOpen ? (
              <li className="nav-link" onClick={handleClickOpen}>
                <span className="Login-Text">Login</span>
              </li>
            ) : (
                null
              )}
          </ul>
        </div>
        <div className={`mainbar ${contentBarOpen}`} id="mainbar">
          <FeedWidget
            {...props}
            feedurl={feedUrl}
            main={main}
            version={version}
            onStateChange={setStateChange}
          />
        </div>
      </div>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Coming Soon"}</DialogTitle>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            OK
          </Button>
        </DialogActions>
      </Dialog>
    </div >
  );
}
const mapStateToProps = (state) => ({
  feed: state.feed
});

export default connect(mapStateToProps)(App);
